package com.class1;

/*
Create a Java program and name it Practice Variables
In your program create variables to store all different types of primitive data to test the range
Print the value of each variable
 
 
 */

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte by = Byte.MAX_VALUE;
		short sh = Short.MAX_VALUE;
		int in = Integer.MAX_VALUE;
		float fl = Float.MAX_VALUE;
		double dou = Double.MAX_VALUE;
		boolean bo = !true;

		System.out.println("This is the Byte " + by);
		System.out.println("This is the Short " + sh);
		System.out.println("This is the int " + in);
		System.out.println("This is the Float " + fl);
		System.out.println("This is the Double " + dou);
		System.out.println("This is the Boolean " + bo);

	}

}
